import Path from 'path'

/**
 * CLI parameters [Short, Full].
 * @type {Object}
 */
const CLI_PARAMS = {
  help: ['-h', '--help'],
  version: ['-v', '--version'],
  input: ['-i', '--input'],
  output: ['-o', '--output'],
  type: ['-t', '--type'],
  modes: ['-m', '--modes'],
  sizes: ['-s', '--sizes'],
  names: ['-n', '--names'],
  report: ['-r', '--report']
}

/**
 * Types of the source file.
 * @type {Object}
 */
const SOURCE_TYPES = {
  svg: 'svg',
  png: 'png'
}

/**
 * Output file modes.
 * @type {Object}
 */
const OUTPUT_MODES = {
  ico: 'ico',
  icns: 'icns',
  favicon: 'favicon',
  all: ['ico', 'icns', 'favicon']
}

/**
 * Text of help.
 * @type {String}
 */
const HELP_TEXT = `
Usage: icon-gen [OPTIONS]

Generate an icon from the SVG or PNG file.

Options:
-h, --help    Display this text.

-v, --version Display the version number.

-i, --input   Path of the SVG file or PNG file directory.

-o, --output  Path of the output directory.

-t, --type    Type of the input file.
              'svg' is the SVG file, 'png' is the PNG files directory.
              Allowed values: svg, png
              Default is 'svg'.

-m, --modes   Mode of the output files.
              Allowed values: ico, icns, favicon, all
              Default is 'all'.

-n, --names   Change an output file names for ICO and ICNS.
              ex: 'ico=foo,icns=bar'
              Default is 'app.ico' and 'app.ico'.

-r, --report  Display the process reports.
              Default is disable.

-s, --sizes   List of sizes to include for ICO and ICNS.
              ex: 'ico=[12,24,32],icns=[12,24,64]'

Examples:
$ icon-gen -i sample.svg -o ./dist -r
$ icon-gen -i ./images -o ./dist -t png -r
$ icon-gen -i sample.svg -o ./dist -m ico,favicon -r
$ icon-gen -i sample.svg -o ./dist -n ico=foo,icns=bar

See also:
https://github.com/akabekobeko/npm-icon-gen`

/**
 * Default command line options.
 * @type {Object}
 */
export const DEFAULT_OPTIONS = {
  type: SOURCE_TYPES.svg,
  modes: OUTPUT_MODES.all,
  names: {
    ico: 'app',
    icns: 'app'
  },
  report: false
}

/**
 * Utility of the commad line interface.
 */
export default class CLI {
  /**
   * Show the help text.
   *
   * @param {WritableStream} stream Target stream.
   *
   * @return {Promise} Promise object.
   */
  static showHelp (stream) {
    return new Promise((resolve) => {
      stream.write(HELP_TEXT)
      resolve()
    })
  }

  /**
   * Show the version number.
   *
   * @param {WritableStream} stream Target stream.
   *
   * @return {Promise} Promise object.
   */
  static showVersion (stream) {
    return new Promise((resolve) => {
      const read = (path) => {
        try {
          return require(path).version
        } catch (err) {
          return null
        }
      }

      const version = read('../package.json') || read('../../package.json')
      stream.write('v' + version + '\n')

      resolve()
    })
  }

  /**
   * Parse for the command line argumens.
   *
   * @param {Array.<String>} argv Arguments of the command line.
   *
   * @return {CLIOptions} Parse results.
   */
  static parse (argv) {
    if (!(argv && 0 < argv.length)) {
      return {help: true}
    }

    switch (argv[0]) {
      case CLI_PARAMS.help[0]:
      case CLI_PARAMS.help[1]:
        return {help: true}

      case CLI_PARAMS.version[0]:
      case CLI_PARAMS.version[1]:
        return {version: true}

      default:
        return CLI._parse(argv)
    }
  }

  /**
   * Parse for the command line argumens.
   *
   * @param {Array.<String>} argv Arguments of the command line.
   *
   * @return {CLIOptions} Parse results.
   */
  static _parse (argv) {
    const options = {}
    argv.forEach((arg, index) => {
      switch (arg) {
        case CLI_PARAMS.input[0]:
        case CLI_PARAMS.input[1]:
          if (index + 1 < argv.length) {
            options.input = Path.resolve(argv[index + 1])
          }
          break

        case CLI_PARAMS.output[0]:
        case CLI_PARAMS.output[1]:
          if (index + 1 < argv.length) {
            options.output = Path.resolve(argv[index + 1])
          }
          break

        case CLI_PARAMS.type[0]:
        case CLI_PARAMS.type[1]:
          if (index + 1 < argv.length) {
            options.type = argv[index + 1]
          }
          break

        case CLI_PARAMS.report[0]:
        case CLI_PARAMS.report[1]:
          options.report = true
          break

        case CLI_PARAMS.modes[0]:
        case CLI_PARAMS.modes[1]:
          if (index + 1 < argv.length) {
            options.modes = CLI._parseMode(argv[index + 1])
          }
          break

        case CLI_PARAMS.names[0]:
        case CLI_PARAMS.names[1]:
          if (index + 1 < argv.length) {
            options.names = CLI._parseNames(argv[index + 1])
          }
          break

        case CLI_PARAMS.sizes[0]:
        case CLI_PARAMS.sizes[1]:
          if (index + 1 < argv.length) {
            options.sizes = CLI._parseSizes(argv[index + 1])
          }
          break

        default:
          break
      }
    })

    if (!(options.type) || (options.type !== SOURCE_TYPES.svg && options.type !== SOURCE_TYPES.png)) {
      options.type = SOURCE_TYPES.svg
    }

    if (!(options.modes)) {
      options.modes = OUTPUT_MODES.all
    }

    if (!(options.sizes)) {
      options.sizes = {}
    }

    return options
  }

  /**
   * Parse for the mode option.
   *
   * @param {String} arg Option. Format is a 'all' or 'ico,icns,favicon'.
   *
   * @return {Array.<String>} Parse results.
   */
  static _parseMode (arg) {
    if (!(arg)) {
      return OUTPUT_MODES.all
    }

    const values = arg.split(',').filter((value) => {
      switch (value) {
        case OUTPUT_MODES.ico:
        case OUTPUT_MODES.icns:
        case OUTPUT_MODES.favicon:
          return true

        default:
          return false
      }
    })

    return (0 < values.length ? values : OUTPUT_MODES.all)
  }

  /**
   * Parse the output file names.
   *
   * @param {String} arg Option. Format is a 'ico=foo,icns=bar'.
   *
   * @return {Object} File names.
   */
  static _parseNames (arg) {
    const names = {}
    if (!(typeof arg === 'string')) {
      return names
    }

    const params = arg.split(',')
    params.forEach((param) => {
      const units = param.split('=')
      if (units.length < 2) {
        return
      }

      const key   = units[0]
      const value = units[1]
      switch (key) {
        case OUTPUT_MODES.ico:
        case OUTPUT_MODES.icns:
          names[key] = value
          break

        default:
          break
      }
    })

    return names
  }

  /**
   * Parse the input file sizes.
   *
   * @param {String} arg Option. Format is a 'ico=[16,24,32],icns=[16,24,32]'.
   *
   * @return {Object} File sizes.
   */
  static _parseSizes (arg) {
    const sizes = {}
    if (!(typeof arg === 'string')) {
      return sizes
    }

    const regexp = new RegExp(/((ico|icns)=\[[0-9,]+\])/g)

    const params = arg.match(regexp)
    params.forEach((param) => {
      const units = param.split('=')
      if (units.length < 2) {
        return
      }

      const key    = units[0]
      const values = units[1].match(/\[([0-9,]+)\]/)[1].split(',')
      switch (key) {
        case OUTPUT_MODES.ico:
        case OUTPUT_MODES.icns:
          sizes[key] = values
          break

        default:
          break
      }
    })

    return sizes
  }
}

#!/usr/bin/env node

import CLI from './cli.js'
import IconGen from '../lib/index.js'

/**
 * Main process.
 *
 * @param {CLIOptions} options Options.
 *
 * @return {Promise} Promise object.
 */
function execute (options) {
  return new Promise((resolve, reject) => {
    if (!(options.input)) {
      return reject(new Error('"-i" or "--input" has not been specified. This parameter is required.'))
    }

    if (!(options.output)) {
      return reject(new Error('"-o" or "--output" has not been specified. This parameter is required.'))
    }

    return IconGen(options.input, options.output, options)
  })
}

/**
 * Entry point of the CLI.
 *
 * @param {Array.<String>} argv   Arguments of the command line.
 * @param {WritableStream} stdout Standard output.
 *
 * @return {Promise} Promise object.
 */
function main (argv, stdout) {
  const options = CLI.parse(argv)
  if (options.help) {
    return CLI.showHelp(stdout)
  } else if (options.version) {
    return CLI.showVersion(stdout)
  }

  return execute(options)
}

main(process.argv.slice(2), process.stdout)
  .then()
  .catch((err) => {
    console.error(err)
  })

import Fs from 'fs'
import Path from 'path'
import Util from './util.js'
import ICOGenerator from './ico-generator.js'

/**
 * Sizes required for the FAVICON PNG files.
 * @type {Array.<Number>}
 */
const REQUIRED_PNG_IMAGE_SIZES =  [32, 57, 72, 96, 120, 128, 144, 152, 195, 228]

/**
 * Sizes required for the FAVICON ICO file.
 * @type {Array.<Number>}
 */
const REQUIRED_ICO_IMAGE_SIZES = [16, 24, 32, 48, 64]

/**
 * Sizes required for the FAVICON files.
 * @type {Array.<Number>}
 */
const REQUIRED_IMAGE_SIZES = REQUIRED_PNG_IMAGE_SIZES.concat(REQUIRED_ICO_IMAGE_SIZES)
  .filter((a, i, self) => self.indexOf(a) === i)
  .sort((a, b) => a - b)

/**
 * File name of the FAVICON file.
 * @type {String}
 */
const ICO_FILE_NAME = 'favicon'

/**
 * Collection of the file name and size of the icon.
 * @type {Array.<Object>}
 * @see https://github.com/audreyr/favicon-cheat-sheet
 */
const PNG_FILE_INFOS = [
  {name: 'favicon-32.png',  size: 32},  // Certain old but not too old Chrome versions mishandle ico
  {name: 'favicon-57.png',  size: 57},  // Standard iOS home screen (iPod Touch, iPhone first generation to 3G)
  {name: 'favicon-72.png',  size: 72},  // iPad home screen icon
  {name: 'favicon-96.png',  size: 96},  // GoogleTV icon
  {name: 'favicon-120.png', size: 120}, // iPhone retina touch icon (Change for iOS 7: up = require(114x114)
  {name: 'favicon-128.png', size: 128}, // Chrome Web Store icon
  {name: 'favicon-144.png', size: 144}, // IE10 Metro tile for pinned site
  {name: 'favicon-152.png', size: 152}, // iPad retina touch icon (Change for iOS 7: up = require(144x144)
  {name: 'favicon-195.png', size: 195}, // Opera Speed Dial icon
  {name: 'favicon-228.png', size: 228}  // Opera Coast icon
]

/**
 * Generate the FAVICON files = require(a PNG images.
 */
export default class FaviconGenerator {
  /**
   * Generate a FAVICON image files (ICO and PNG) from the PNG images.
   *
   * @param {Array.<ImageInfo>} images File information for the PNG files generation.
   * @param {String}            dir    Output destination the path of directory.
   * @param {Logger}            logger Logger.
   *
   * @return {Promise} Promise object.
   */
  static generate (images, dir, logger) {
    const results = []
    return Promise
      .resolve()
      .then(() => {
        return FaviconGenerator._generateICO(images, dir, logger)
      })
      .then((icoFile) => {
        results.push(icoFile)
        return FaviconGenerator._generatePNG(images, dir, logger)
      })
      .then((pngFiles) => {
        return results.concat(pngFiles)
      })
  }

  /**
   * Get the size of the required PNG.
   *
   * @return {Array.<Number>} Sizes.
   */
  static getRequiredImageSizes () {
    return REQUIRED_IMAGE_SIZES
  }

  /**
   * Generate the FAVICON file from the PNG images.
   *
   * @param {Array.<ImageInfo>} images File information for the PNG files generation.
   * @param {String}            dir    Output destination the path of directory.
   * @param {Logger}            logger Logger.
   *
   * @return {Promise} Promise object.
   */
  static _generateICO (images, dir, logger) {
    const options = {names: {ico: ICO_FILE_NAME}}
    return ICOGenerator.generate(Util.filterImagesBySizes(images, REQUIRED_ICO_IMAGE_SIZES), dir, options, logger)
  }

  /**
   * Generate the FAVICON PNG file from the PNG images.
   *
   * @param {Array.<ImageInfo>} images File information for the PNG files generation.
   * @param {String}            dir    Output destination the path of directory.
   * @param {Logger}            logger Logger.
   *
   * @return {Promise} Promise object.
   */
  static _generatePNG (images, dir, logger) {
    return new Promise((resolve, reject) => {
      logger.log('Favicon:')

      // PNG
      const tasks = Util.filterImagesBySizes(images, REQUIRED_PNG_IMAGE_SIZES)
        .map((image) => {
          return FaviconGenerator._copyImage(image, dir, logger)
        })

      Promise
        .all(tasks)
        .then((results) => {
          resolve(results)
        })
        .catch((err) => {
          reject(err)
        })
    })
  }

  /**
   * Copy to image.
   *
   * @param {ImageInfo} image  Image information.
   * @param {String}    dir    Output destination The path of directory.
   * @param {Logger}    logger Logger.
   *
   * @return {Promise} Task to copy an image.
   */
  static _copyImage (image, dir, logger) {
    return new Promise((resolve, reject) => {
      const fileName = FaviconGenerator._fileNameFromSize(image.size)
      if (!(fileName)) {
        // Unknown target is ignored
        resolve('')
        return
      }

      const reader = Fs.createReadStream(image.path)
        .on('error', (err) => {
          reject(err)
        })

      const dest   = Path.join(dir, fileName)
      const writer = Fs.createWriteStream(dest)
        .on('error', (err) => {
          reject(err)
        })
        .on('close', () => {
          logger.log('  Create: ' + dest)
          resolve(dest)
        })

      reader.pipe(writer)
    })
  }

  /**
   * Get the file names corresponding to image size.
   *
   * @param {Number} size Size of an image.
   *
   * @return {String} If successful name, otherwise null.
   */
  static _fileNameFromSize (size) {
    let name = null
    PNG_FILE_INFOS.some((png) => {
      if (png.size === size) {
        name = png.name
        return true
      }

      return false
    })

    return name
  }
}

import Fs from 'fs'
import Path from 'path'
import Util from './util.js'
import {PNG} from 'pngjs'
import RLE from './rle.js'
import { Buffer } from 'buffer'
import { Promise } from 'es6-promise'

/**
 * Sizes required for the ICNS file.
 * @type {Array}
 */
const REQUIRED_IMAGE_SIZES = [16, 32, 64, 128, 256, 512, 1024]

/**
 * The size of the ICNS header.
 * @type {Number}
 */
const HEADER_SIZE = 8

/**
 * Identifier of the ICNS file, in ASCII "icns".
 * @type {Number}
 */
const FILE_HEADER_ID = 'icns'

/**
 * ICNS file extension.
 * @type {String}
 */
const FILE_EXTENSION = '.icns'

/**
 * Information of the images, Mac OS 8.x (il32, is32, l8mk, s8mk) is unsupported.
 * If icp4, icp5, icp6 is present, Icon will not be supported because it can not be set as Folder of Finder.
 *
 * @type {Array.<ICNSIconInfo>}
 */
const ICON_INFOS = [
  // Normal
  {id: 'ic07', size: 128},
  {id: 'ic08', size: 256},
  {id: 'ic09', size: 512},
  {id: 'ic10', size: 1024},

  // Retina
  {id: 'ic11', size: 32},
  {id: 'ic12', size: 64},
  {id: 'ic13', size: 256},
  {id: 'ic14', size: 512},

  // Mac OS 8.5
  {id: 'is32', mask: 's8mk', size: 16},
  {id: 'il32', mask: 'l8mk', size: 32}
]

/**
 * Generate the ICNS file from a PNG images.
 * However, Mac OS 8.x is unsupported.
 */
export default class ICNSGenerator {
  /**
   * Create the ICNS file from a PNG images.
   *
   * @param {Array.<ImageInfo>} images Information of the image files.
   * @param {String}            dir     Output destination the path of directory.
   * @param {Object}            options Options.
   * @param {Logger}            logger  Logger.
   *
   * @return {Promise} Promise object.
   */
  static generate (images, dir, options, logger) {
    return new Promise((resolve, reject) => {
      logger.log('ICNS:')

      const dest    = Path.join(dir, options.names.icns + FILE_EXTENSION)
      const targets = Util.filterImagesBySizes(images, Util.checkImageSizes(REQUIRED_IMAGE_SIZES, options, 'icns'))

      if (ICNSGenerator._createIcon(targets, dest)) {
        logger.log('  Create: ' + dest)
        resolve(dest)
      } else {
        Fs.unlinkSync(dest)
        reject(new Error('Faild to read/write image.'))
      }
    })
  }

  /**
   * Get the size of the required PNG.
   *
   * @return {Array.<Number>} Sizes.
   */
  static getRequiredImageSizes () {
    return REQUIRED_IMAGE_SIZES
  }

  /**
   * Creat a file header and icon blocks.
   *
   * @param {Array.<ImageInfo>} images Information of the image files.
   * @param {String}            dest   The path of the output destination file.
   *
   * @return {Boolean} "true" if it succeeds.
   */
  static _createIcon (images, dest) {
    // Write a temporary file size
    let fileSize = HEADER_SIZE
    let stream   = Fs.createWriteStream(dest)
    stream.write(ICNSGenerator._createFileHeader(fileSize), 'binary')

    for (let i = 0, max = ICON_INFOS.length; i < max; ++i) {
      const info = ICON_INFOS[i]
      const image = ICNSGenerator._imageFromIconSize(info.size, images)
      if (!(image)) {
        // Depending on the command line option, there may be no corresponding size
        continue
      }

      let block = null
      switch (info.id) {
        case 'is32':
        case 'il32':
          block = ICNSGenerator._createIconBlockPackBits(info.id, info.mask, Fs.readFileSync(image.path))
          break

        default:
          block = ICNSGenerator._createIconBlock(info.id, Fs.readFileSync(image.path))
          break
      }

      if (block) {
        stream.write(block, 'binary')
        fileSize += block.length
      } else {
        fileSize = 0
        break
      }
    }

    stream.end()

    if (fileSize === 0) {
      return false
    }

    // Update an actual file size
    stream = Fs.createWriteStream(dest)
    stream.write(ICNSGenerator._createFileHeader(fileSize), 'binary')
    stream.end()

    return true
  }

  /**
   * Create an icon block's data.
   *
   * @param {String} id   Identifier of the icon.
   * @param {Buffer} data Body data of the icon.
   *
   * @return {Buffer} data.
   */
  static _createIconBlock (id, data) {
    if (!(data)) {
      return null
    }

    const header = ICNSGenerator._createIconHeader(id, data.length)
    return Buffer.concat([header, data], header.length + data.length)
  }

  /**
   * Create an icon blocks (Color and mask) for PackBits.
   *
   * @param {String} id   Identifier of the icon (color block).
   * @param {String} mask Identifier of the icon (mask block).
   * @param {Buffer} data Binary of the PNG image.
   *
   * @return {Buffer} If successful it wrote the icon block. "null" on failure.
   */
  static _createIconBlockPackBits (id, mask, data) {
    const bodies = ICNSGenerator._createIconBlockPackBitsBodies(data)
    if (!(bodies)) {
      return null
    }

    const colorBlock = ICNSGenerator._createIconBlock(id, Buffer.from(bodies.colors))
    const maskBlock  = ICNSGenerator._createIconBlock(mask, Buffer.from(bodies.masks))

    return Buffer.concat([colorBlock, maskBlock], colorBlock.length + maskBlock.length)
  }

  /**
   * Create a color and mask data.
   *
   * @param {ImageInfo} data Information of the image.
   *
   * @return {Object} Bodies, "color" is a color (Compressed by ICNS RLE), "mask" is a mask.
   */
  static _createIconBlockPackBitsBodies (data) {
    if (!(data)) {
      return null
    }

    const png     = PNG.sync.read(data)
    const results = {colors: [], masks: []}
    const r = []
    const g = []
    const b = []

    for (let i = 0, max = png.data.length; i < max; i += 4) {
      // RGB
      r.push(png.data.readUInt8(i))
      g.push(png.data.readUInt8(i + 1))
      b.push(png.data.readUInt8(i + 2))

      // Alpha
      results.masks.push(png.data.readUInt8(i + 3))
    }

    // Compress
    results.colors = results.colors.concat(RLE.packICNS(r))
    results.colors = results.colors.concat(RLE.packICNS(g))
    results.colors = results.colors.concat(RLE.packICNS(b))

    return results
  }

  /**
   * Create the ICNS file header.
   *
   * @param {Number} fileSize File size.
   *
   * @return {Buffer} Header data.
   */
  static _createFileHeader (fileSize) {
    const buffer = Buffer.alloc(HEADER_SIZE)
    buffer.write(FILE_HEADER_ID, 0, 'ascii')
    buffer.writeUInt32BE(fileSize, 4)

    return buffer
  }

  /**
   * Create the Icon header in ICNS file.
   *
   * @param {Object} id        Identifier of the icon.
   * @param {Number} imageSize Size of the image data.
   *
   * @return {Buffer} Header data.
   */
  static _createIconHeader (id, imageSize) {
    const buffer = Buffer.alloc(HEADER_SIZE)
    buffer.write(id, 0, 'ascii')
    buffer.writeUInt32BE(HEADER_SIZE + imageSize, 4)

    return buffer
  }

  /**
   * Select the support image from the icon size.
   *
   * @param {Number}            size   Sizo of icon.
   * @param {Array.<ImageInfo>} images File informations..
   *
   * @return {ImageInfo} If successful image information, otherwise null.
   */
  static _imageFromIconSize (size, images) {
    let result = null
    images.some((image) => {
      if (image.size === size) {
        result = image
        return true
      }

      return false
    })

    return result
  }

  /**
   * Unpack an icon block files from ICNS file (For debug).
   *
   * @param {String} src  Path of the ICNS file.
   * @param {String} dest Path of directory to output icon block files.
   *
   * @return {Promise} Promise object.
   */
  static _debugUnpackIconBlocks (src, dest) {
    return new Promise((resolve, reject) => {
      Fs.readFile(src, (err, data) => {
        if (err) {
          return reject(err)
        }

        for (let pos = HEADER_SIZE, max = data.length; pos < max;) {
          const header = data.slice(pos, pos + HEADER_SIZE)
          const id     = header.toString('ascii', 0, 4)
          const size   = header.readUInt32BE(4) - HEADER_SIZE

          pos += HEADER_SIZE
          const body  = data.slice(pos, pos + size)
          Fs.writeFileSync(Path.join(dest, id + '.header'), header, 'binary')
          Fs.writeFileSync(Path.join(dest, id + '.body'), body, 'binary')

          pos += size
        }

        resolve()
      })
    })
  }
}

import Fs from 'fs'
import Path from 'path'
import Util from './util.js'
import {PNG} from 'pngjs'

/**
 * Sizes required for the ICO file.
 * @type {Array}
 */
const REQUIRED_IMAGE_SIZES =  [16, 24, 32, 48, 64, 128, 256]

/**
 * ICNO file extension.
 * @type {String}
 */
const FILE_EXTENSION = '.ico'

/**
 * Size of the file header.
 * @type {Number}
 */
const HEADER_SIZE = 6

/**
 * Size of the icon directory.
 * @type {Number}
 */
const DIRECTORY_SIZE = 16

/**
 * Size of the BITMAPINFOHEADER.
 * @type {Number}
 */
const BITMAPINFOHEADER_SIZE = 40

/**
 * Color mode.
 * @type {Number}
 */
const BI_RGB =  0

/**
 * Generate the ICO file from PNG images.
 */
export default class ICOGenerator {
  /**
   * Generate the ICO file from a PNG images.
   *
   * @param {Array.<ImageInfo>} images  File informations..
   * @param {String}            dir     Output destination the path of directory.
   * @param {Object}            options Options.
   * @param {Logger}            logger  Logger.
   *
   * @return {Promise} Promise object.
   */
  static generate (images, dir, options, logger) {
    return new Promise((resolve) => {
      logger.log('ICO:')

      const dest    = Path.join(dir, options.names.ico + FILE_EXTENSION)
      const targets = Util.filterImagesBySizes(images, Util.checkImageSizes(REQUIRED_IMAGE_SIZES, options, 'ico'))
      const stream  = Fs.createWriteStream(dest)
      stream.write(ICOGenerator._createFileHeader(targets.length), 'binary')

      const pngs = targets.map((image) => {
        const data = Fs.readFileSync(image.path)
        return PNG.sync.read(data)
      })

      let offset = HEADER_SIZE + (DIRECTORY_SIZE * targets.length)
      pngs.forEach((png) => {
        const directory = ICOGenerator._createDirectory(png, offset)
        stream.write(directory, 'binary')
        offset += png.data.length + BITMAPINFOHEADER_SIZE
      })

      pngs.forEach((png) => {
        const header = ICOGenerator._createBitmapInfoHeader(png, BI_RGB)
        stream.write(header, 'binary')

        const dib = ICOGenerator._convertPNGtoDIB(png.data, png.width, png.height, png.bpp)
        stream.write(dib, 'binary')
      })

      stream.end()

      logger.log('  Create: ' + dest)
      resolve(dest)
    })
  }

  /**
   * Get the size of the required PNG.
   *
   * @return {Array.<Number>} Sizes.
   */
  static getRequiredImageSizes () {
    return REQUIRED_IMAGE_SIZES
  }

  /**
   * Create the ICO file header.
   *
   * @param {Number} count  Specifies number of images in the file.
   *
   * @return {Buffer} Header data.
   *
   * @see https://msdn.microsoft.com/en-us/library/ms997538.aspx
   */
  static _createFileHeader (count) {
    const b = Buffer.alloc(HEADER_SIZE)
    b.writeUInt16LE(0, 0)     // 2 WORD Reserved
    b.writeUInt16LE(1,  2)    // 2 WORD Type
    b.writeUInt16LE(count, 4) // 2 WORD Image count

    return b
  }

  /**
   * Create the Icon entry.
   *
   * @param {Object} png    PNG image.
   * @param {Number} offset The offset of directory data from the beginning of the ICO/CUR file
   *
   * @return {Buffer} Directory data.
   *
   * @see https://msdn.microsoft.com/en-us/library/ms997538.aspx
   */
  static _createDirectory (png, offset) {
    const b      = Buffer.alloc(DIRECTORY_SIZE)
    const size   = png.data.length + BITMAPINFOHEADER_SIZE
    const width  = (256 <= png.width ? 0 : png.width)
    const height = (256 <= png.height ? 0 : png.height)
    const bpp    = png.bpp * 8

    b.writeUInt8(width, 0)      // 1 BYTE  Image width
    b.writeUInt8(height, 1)     // 1 BYTE  Image height
    b.writeUInt8(0, 2)          // 1 BYTE  Colors
    b.writeUInt8(0, 3)          // 1 BYTE  Reserved
    b.writeUInt16LE(1, 4)       // 2 WORD  Color planes
    b.writeUInt16LE(bpp, 6)     // 2 WORD  Bit per pixel
    b.writeUInt32LE(size, 8)    // 4 DWORD Bitmap (DIB) size
    b.writeUInt32LE(offset, 12) // 4 DWORD Offset

    return b
  }

  /**
   * Create the BITMAPINFOHEADER.
   *
   * @param {Object} png         PNG image.
   * @param {Number} compression Compression mode
   *
   * @return {Buffer} BITMAPINFOHEADER data.
   *
   * @see https://msdn.microsoft.com/ja-jp/library/windows/desktop/dd183376%28v=vs.85%29.aspx
   */
  static _createBitmapInfoHeader (png, compression) {
    const b = Buffer.alloc(BITMAPINFOHEADER_SIZE)
    b.writeUInt32LE(BITMAPINFOHEADER_SIZE, 0) // 4 DWORD biSize
    b.writeInt32LE(png.width, 4)              // 4 LONG  biWidth
    b.writeInt32LE(png.height * 2, 8)         // 4 LONG  biHeight
    b.writeUInt16LE(1, 12)                    // 2 WORD  biPlanes
    b.writeUInt16LE(png.bpp * 8, 14)          // 2 WORD  biBitCount
    b.writeUInt32LE(compression, 16)          // 4 DWORD biCompression
    b.writeUInt32LE(png.data.length, 20)      // 4 DWORD biSizeImage
    b.writeInt32LE(0, 24)                     // 4 LONG  biXPelsPerMeter
    b.writeInt32LE(0, 28)                     // 4 LONG  biYPelsPerMeter
    b.writeUInt32LE(0, 32)                    // 4 DWORD biClrUsed
    b.writeUInt32LE(0, 36)                    // 4 DWORD biClrImportant

    return b
  }

  /**
   * Convert a PNG of the byte array to the DIB (Device Independent Bitmap) format.
   *
   * PNG in color RGBA (and more), the coordinate structure is the Top/Left to Bottom/Right.
   * DIB in color BGRA, the coordinate structure is the Bottom/Left to Top/Right.
   *
   * @param {Buffer} src    Target image.
   * @param {Number} width  The width of the image.
   * @param {Number} height The height of the image.
   * @param {Number} bpp    The bit per pixel of the image.
   *
   * @return {Buffer} Converted image
   *
   * @see https://en.wikipedia.org/wiki/BMP_file_format
   */
  static _convertPNGtoDIB (src, width, height, bpp) {
    const cols   = (width * bpp)
    const rows   = (height * cols)
    const rowEnd = (rows - cols)
    const dest   = Buffer.alloc(src.length)

    for (let row = 0; row < rows; row += cols) {
      for (let col = 0; col < cols; col += bpp) {
        // RGBA: Top/Left -> Bottom/Right
        let pos = row + col
        const r = src.readUInt8(pos)
        const g = src.readUInt8(pos + 1)
        const b = src.readUInt8(pos + 2)
        const a = src.readUInt8(pos + 3)

        // BGRA: Right/Left -> Top/Right
        pos = (rowEnd - row) + col
        dest.writeUInt8(b, pos)
        dest.writeUInt8(g, pos + 1)
        dest.writeUInt8(r, pos + 2)
        dest.writeUInt8(a, pos + 3)
      }
    }

    return dest
  }
}

import Fs from 'fs'
import Path from 'path'
import Del from 'del'
import MkdirP from 'mkdirp'
import PngGenerator from './png-generator.js'
import ICOGenerator from './ico-generator.js'
import ICNSGenerator from './icns-generator.js'
import FaviconGenerator from './favicon-generator.js'
import Util from './util.js'

/**
 * Generate an icons.
 */
export default class IconGenerator {
  /**
   * Generate an icon = require(the SVG file.
   *
   * @param {String} src     Path of the SVG file.
   * @param {String} dir     Path of the output files directory.
   * @param {Object} options Options.
   * @param {Logger} logger  Logger.
   *
   * @return {Promise} Promise object.
   */
  static fromSVG (src, dir, options, logger) {
    return new Promise((resolve, reject) => {
      const svgFilePath = Path.resolve(src)
      const destDirPath = Path.resolve(dir)
      logger.log('Icon generator from SVG:')
      logger.log('  src: ' + svgFilePath)
      logger.log('  dir: ' + destDirPath)

      const workDir = Util.createWorkDir()
      if (!(workDir)) {
        reject(new Error('Failed to create the working directory.'))
        return
      }

      PngGenerator.generate(svgFilePath, workDir, options.modes, (err, images) => {
        if (err) {
          Del.sync([workDir], {force: true})
          reject(err)
          return
        }

        IconGenerator._generate(images, destDirPath, options, logger, (err2, results) => {
          Del.sync([workDir], {force: true})
          return (err2 ? reject(err2) : resolve(results))
        })
      }, logger)
    })
  }

  /**
   * Generate an icon = require(the SVG file.
   *
   * @param {String} src     Path of the PNG files direcgtory.
   * @param {String} dir     Path of the output files directory.
   * @param {Object} options Options.
   * @param {Logger} logger  Logger.
   *
   * @return {Promise} Promise object.
   */
  static fromPNG (src, dir, options, logger) {
    return new Promise((resolve, reject) => {
      const pngDirPath  = Path.resolve(src)
      const destDirPath = Path.resolve(dir)
      logger.log('Icon generetor from PNG:')
      logger.log('  src: ' + pngDirPath)
      logger.log('  dir: ' + destDirPath)

      const images = IconGenerator._getRequiredImageSizes(options.modes, options.sizes)
        .map((size) => {
          return Path.join(pngDirPath, size + '.png')
        })
        .map((path) => {
          const size = Number(Path.basename(path, '.png'))
          return { path, size }
        })

      let notExistsFile = null
      images.some((image) => {
        const stat = Fs.statSync(image.path)
        if (!(stat && stat.isFile())) {
          notExistsFile = Path.basename(image.path)
          return true
        }

        return false
      })

      if (notExistsFile) {
        reject(new Error('"' + notExistsFile + '" does not exist.'))
        return
      }

      IconGenerator._generate(images, dir, options, logger, (err, results) => {
        return (err ? reject(err) : resolve(results))
      })
    })
  }

  static _getRequiredImageSizes (modes, sizes) {
    if (!(sizes)) {
      return PngGenerator.getRequiredImageSizes(modes)
    }

    let imageSizes = []
    modes.forEach((mode) => {
      if (sizes[mode]) {
        imageSizes = imageSizes.concat(sizes[mode])
      }
    })

    return 0 < imageSizes.length ? imageSizes : PngGenerator.getRequiredImageSizes(modes)
  }

  /**
   * Generate an icon = require(the image file infromations.
   *
   * @param {Array.<ImageInfo>} images  Image file informations.
   * @param {String}            dest    Destination directory path.
   * @param {Object}            options Options.
   * @param {Logger}            logger  Logger.
   * @param {Function}          cb      Callback function.
   */
  static _generate (images, dest, options, logger, cb) {
    if (!(images && 0 < images.length)) {
      cb(new Error('Targets is empty.'))
      return
    }

    const dir = Path.resolve(dest)
    MkdirP.sync(dir)

    // Select output mode
    const tasks = []
    options.modes.forEach((mode) => {
      switch (mode) {
        case 'icns':
          tasks.push(ICNSGenerator.generate(images, dir, options, logger))
          break

        case 'ico':
          tasks.push(ICOGenerator.generate(images, dir, options, logger))
          break

        case 'favicon':
          tasks.push(FaviconGenerator.generate(images, dir, logger))
          break

        default:
          break
      }
    })

    Promise
      .all(tasks)
      .then((results) => {
        cb(null, Util.flattenValues(results))
      })
      .catch((err) => {
        cb(err)
      })
  }

  /**
   * Get the icon sizes.
   *
   * @param {Array.<Number>} defaltSizes Sizes of the defalt.
   * @param {Object}         options     CLI options.
   * @param {String}         type        Type of the icon, 'ico' or 'icns'.
   *
   * @return {Array.<Number>} Sizes.
   */
  static _getSizes (defaltSizes, options, type) {
    return options && options.sizes && options.sizes[type] ? options.sizes[type] : defaltSizes
  }
}

import IconGenerator from './icon-generator.js'
import Logger from './logger.js'
import {DEFAULT_OPTIONS} from '../bin/cli.js'

/**
 * Generate an icon = require(the SVG file.
 *
 * @param {String} src     SVG file path.
 * @param {String} dest    Destination directory path.
 * @param {Object} options Options.
 */
module.exports = function (src, dest, options = DEFAULT_OPTIONS) {
  const opt = options
  if (!(opt.modes)) {
    opt.modes = DEFAULT_OPTIONS.modes
  }

  if (!(opt.names)) {
    opt.names = DEFAULT_OPTIONS.names
  }

  if (!(opt.names.ico)) {
    opt.names.ico = DEFAULT_OPTIONS.names.ico
  }

  if (!(opt.names.icns)) {
    opt.names.icns = DEFAULT_OPTIONS.names.icns
  }

  const logger = new Logger(opt.report)
  switch (opt.type) {
    case 'png':
      return IconGenerator.fromPNG(src, dest, opt, logger)

    default:
      return IconGenerator.fromSVG(src, dest, opt, logger)
  }
}

/**
 * Display the log message for the stdout.
 */
export default class Logger {
  /**
   * Initialize instance.
   *
   * @param {Boolean} available "true" to display the report, default is "false".
   */
  constructor (available) {
    this._available = available
  }

  /**
   * Display a log message for the stdout.
   *
   * @param {Array.<Object>} args Message arguments.
   */
  log (...args) {
    if (this._available) {
      console.log(...args)
    }
  }

  /**
   * Display an error message for the stdout.
   *
   * @param {Array.<Object>} args Message arguments.
   */
  error (...args) {
    if (this._available) {
      console.error(...args)
    }
  }
}

import Fs from 'fs'
import Path from 'path'
import SVG2PNG from 'svg2png'
import FaviconGenerator from './favicon-generator.js'
import ICNSGenerator from './icns-generator.js'
import ICOGenerator from './ico-generator.js'

/**
 * Generate the PNG files = require(SVG file.
 */
export default class PNGGenerator {
  /**
   * Generate the PNG files = require(the SVG file.
   *
   * @param {String}         src    SVG file path.
   * @param {String}         dir    Output destination The path of directory.
   * @param {Array.<String>} modes  Modes of an output files.
   * @param {Function}       cb     Callback function.
   * @param {Logger}         logger Logger.
   */
  static generate (src, dir, modes, cb, logger) {
    Fs.readFile(src, (err, svg) => {
      if (err) {
        cb(err)
        return
      }

      logger.log('SVG to PNG:')

      const sizes = PNGGenerator.getRequiredImageSizes(modes)
      Promise
        .all(sizes.map((size) => {
          return PNGGenerator._generatePNG(svg, size, dir, logger)
        }))
        .then((results) => {
          cb(null, results)
        })
        .catch((err2) => {
          cb(err2)
        })
    })
  }

  /**
   * Gets the size of the images needed to create an icon.
   *
   * @param {Array.<String>} modes Modes of an output files.
   *
   * @return {Array.<Number>} The sizes of the image.
   */
  static getRequiredImageSizes (modes) {
    let sizes = []
    if (modes && 0 < modes.length) {
      modes.forEach((mode) => {
        switch (mode) {
          case 'icns':
            sizes = sizes.concat(ICNSGenerator.getRequiredImageSizes())
            break

          case 'ico':
            sizes = sizes.concat(ICOGenerator.getRequiredImageSizes())
            break

          case 'favicon':
            sizes = sizes.concat(FaviconGenerator.getRequiredImageSizes())
            break

          default:
            break
        }
      })
    }

    // 'all' mode
    if (sizes.length === 0) {
      sizes = FaviconGenerator.getRequiredImageSizes().concat(ICNSGenerator.getRequiredImageSizes().concat(ICOGenerator.getRequiredImageSizes()))
    }

    return sizes
      .filter((value, index, array) => {
        return (array.indexOf(value) === index)
      })
      .sort((a, b) => {
      // Always ensure the ascending order
        return (a - b)
      })
  }

  /**
   * Generate the PNG file = require(the SVG data.
   *
   * @param {Buffer} svg    SVG data that has been parse by svg2png.
   * @param {Number} size   The size (width/height) of the image.
   * @param {String} dir    Path of the file output directory.
   * @param {Logger} logger Logger.
   *
   * @return {Promise} Image generation task.
   */
  static _generatePNG (svg, size, dir, logger) {
    return new Promise((resolve, reject) => {
      if (!(svg && 0 < size && dir)) {
        reject(new Error('Invalid parameters.'))
        return
      }

      const dest = Path.join(dir, size + '.png')
      logger.log('  Create: ' + dest)

      const buffer = SVG2PNG.sync(svg, { width: size, height: size })
      if (!(buffer)) {
        reject(new Error('Faild to write the image, ' + size + 'x' + size))
        return
      }

      Fs.writeFile(dest, buffer, (err) => {
        if (err) {
          reject(err)
          return
        }

        resolve({ size: size, path: dest })
      })
    })
  }
}

/**
 * Max length of PackBits literal.
 * @type {Number}
 */
const MAX_LITERAL_LENGTH = 127

/**
 * It pack compress/decompress based on RLE (Run Length Encoding).
 */
export default class RLE {
  /**
   * Compress binary with PackBits.
   * This method port Geeks with Blogs's code (Apache License v2.0) to Node.
   *
   * @param {Array.<Number>} src Source binary.
   *
   * @return {Array.<Number>} Compressed binary.
   *
   * @see https://en.wikipedia.org/wiki/PackBits
   * @see http://geekswithblogs.net/rakker/archive/2015/12/14/packbits-in-c.aspx
   */
  static packBits (src) {
    if (!(src && src.length && 0 < src.length)) {
      return []
    }

    let dest     = []
    let literals = []

    for (let i = 0, max = src.length; i < max; ++i) {
      const current = RLE._toUInt8(src[i])
      if (i + 1 < max) {
        const next = RLE._toUInt8(src[i + 1])
        if (current === next) {
          dest = dest.concat(RLE._packBitsLiteralToResult(literals))
          literals = []

          const maxJ   = (max <= i + MAX_LITERAL_LENGTH ? max - i - 1 : MAX_LITERAL_LENGTH)
          let   hitMax = true
          let   runLength = 1

          for (let j = 2; j <= maxJ; ++j) {
            const run = src[i + j]
            if (current !== run) {
              hitMax = false
              const count = RLE._toUInt8(0 - runLength)
              i += j - 1
              dest.push(count)
              dest.push(current)
              break
            }

            ++runLength
          }

          if (hitMax) {
            dest.push(RLE._toUInt8(0 - maxJ))
            dest.push(current)
            i += maxJ
          }
        } else {
          literals.push(current)
          if (literals.length === MAX_LITERAL_LENGTH) {
            dest = dest.concat(RLE._packBitsLiteralToResult(literals))
            literals = []
          }
        }
      } else {
        literals.push(current)
        dest = dest.concat(RLE._packBitsLiteralToResult(literals))
        literals = []
      }
    }

    return dest
  }

  /**
   * Compress binary with ICNS RLE.
   *
   * @param {Array.<Number>} src Source binary.
   *
   * @return {Array.<Number>} Compressed binary.
   *
   * @see https://github.com/fiji/IO/blob/master/src/main/java/sc/fiji/io/icns/RunLengthEncoding.java
   */
  static packICNS (src) {
    // If it is not redundant, keep the size large enough to increase the size
    const packedData = (new Array(src.length * 2)).fill(0)

    let output = 0
    for (let input = 0; input < src.length;) {
      let literalStart = input
      let currentData  = src[input++]

      // Read up to 128 literal bytes
      // Stop if 3 or more consecutive bytes are equal or EOF is reached
      let readBytes     = 1
      let repeatedBytes = 0
      while (input < src.length && readBytes < 128 && repeatedBytes < 3) {
        const nextData = src[input++]
        if (nextData === currentData) {
          if (repeatedBytes === 0) {
            repeatedBytes = 2
          } else {
            repeatedBytes++
          }
        } else {
          repeatedBytes = 0
        }

        readBytes++
        currentData = nextData
      }

      let literalBytes = 0
      if (repeatedBytes < 3) {
        literalBytes = readBytes
        repeatedBytes = 0
      } else {
        literalBytes = readBytes - repeatedBytes
      }

      // Write the literal bytes that were read
      if (0 < literalBytes) {
        packedData[output++] = RLE._toUInt8(literalBytes - 1)
        RLE._arrayCopy(src, literalStart, packedData, output, literalBytes)
        output += literalBytes
      }

      // Read up to 130 consecutive bytes that are equal
      while (input < src.length && src[input] === currentData && repeatedBytes < 130) {
        repeatedBytes++
        input++
      }

      if (3 <= repeatedBytes) {
        // Write the repeated bytes if there are 3 or more
        packedData[output++] = RLE._toUInt8(repeatedBytes + 125)
        packedData[output++] = currentData
      } else {
        // Else move back the in pointer to ensure the repeated bytes are included in the next literal string
        input -= repeatedBytes
      }
    }

    // Trim to the actual size
    const dest = (new Array(output)).fill(0)
    RLE._arrayCopy(packedData, 0, dest, 0, output)

    return dest
  }

  /**
   * Decompress PackBits compressed binary.
   * This method port Geeks with Blogs's code (Apache License v2.0) to Node.
   *
   * @param {Array.<Number>} src Source binary.
   *
   * @return {Array.<Number>} Decompressed binary.
   *
   * @see https://en.wikipedia.org/wiki/PackBits
   * @see http://geekswithblogs.net/rakker/archive/2015/12/14/packbits-in-c.aspx
   */
  static unpackBits (src) {
    const dest = []
    for (let i = 0, max = src.length; i < max; ++i) {
      const count = RLE._toInt8(RLE._toUInt8(src[i]))
      if (count === -128) {
        // Do nothing, skip it
      } else if (0 <= count) {
        const total = count + 1
        for (let j = 0; j < total; ++j) {
          dest.push(RLE._toUInt8(src[i + j + 1]))
        }

        i += total
      } else {
        const total = Math.abs(count) + 1
        for (let j = 0; j < total; ++j) {
          dest.push(RLE._toUInt8(src[i + 1]))
        }

        ++i
      }
    }

    return dest
  }

  /**
   * Copies the array to the target array at the specified position and size.
   *
   * @param {Array.<Number>} src       Byte array of copy source.
   * @param {Number}         srcBegin  Copying start position of source.
   * @param {Array.<Number>} dest      Bayte array of copy destination.
   * @param {Number}         destBegin Writing start position of destinnation.
   * @param {Number}         size      Size of copy bytes.
   */
  static _arrayCopy (src, srcBegin, dest, destBegin, size) {
    if (src.length <= srcBegin || src.length < size || dest.length <= destBegin || dest.length < size) {
      return
    }

    for (let i = srcBegin, j = destBegin, k = 0; k < size; ++i, ++j, ++k) {
      dest[j] = src[i]
    }
  }

  /**
   * Convert PackBits literals to resuls.
   *
   * @param {Array.<Number>} literals PackBits literals.
   *
   * @return {Array.<Number>} Converted literals.
   */
  static _packBitsLiteralToResult (literals) {
    return literals.length === 0 ? [] : [RLE._toUInt8(literals.length - 1)].concat(literals)
  }

  /**
   * Convert a 8bit signed value to unsigned value.
   *
   * @param {Number} value 8bit signed value (-127 to 127)
   *
   * @return {Number} Unsigned value (0 to 255).
   */
  static _toUInt8 (value) {
    return value & 0xFF
  }

  /**
   * Convert a 8bit unsigned value to signed value.
   *
   * @param {Number} value 8bit unsigned value (0 to 255).
   *
   * @return {Number} Signed value (-127 to 127).
   *
   * @see https://github.com/inexorabletash/polyfill/blob/master/typedarray.js
   */
  static _toInt8 (value) {
    return (value << 24) >> 24
  }
}

import Fs from 'fs'
import Path from 'path'
import OS from 'os'
import UUID from 'uuid'

export default class Util {
  /**
   * Create the work directory.
   *
   * @return {String} The path of the created directory, failure is null.
   */
  static createWorkDir () {
    const dir = Path.join(OS.tmpdir(), UUID.v4())
    Fs.mkdirSync(dir)

    const stat = Fs.statSync(dir)
    return (stat && stat.isDirectory() ? dir : null)
  }

  /**
   * Delete a files.
   *
   * @param {Array.<String>} paths File paths.
   */
  static deleteFiles (paths) {
    paths.forEach((path) => {
      try {
        const stat = Fs.statSync(path)
        if (stat && stat.isFile()) {
          Fs.unlinkSync(path)
        }
      } catch (err) {
        console.error(err)
      }
    })
  }

  /**
   * Filter by size to the specified image informations.
   *
   * @param {Array.<ImageInfo>} images Image file informations.
   * @param {Array.<Number>}    sizes  Required sizes.
   *
   * @return {Array.<ImageInfo>} Filtered image informations.
   */
  static filterImagesBySizes (images, sizes) {
    return images
      .filter((image) => {
        return sizes.some((size) => {
          return (image.size === size)
        })
      })
      .sort((a, b) => {
        return (a.size - b.size)
      })
  }

  /**
   * Convert a values to a flat array.
   *
   * @param  {Array.<String|Array>} values Values ([ 'A', 'B', [ 'C', 'D' ] ]).
   *
   * @return {Array.<String>} Flat array ([ 'A', 'B', 'C', 'D' ]).
   */
  static flattenValues (values) {
    const paths = []
    values.forEach((value) => {
      if (!(value)) {
        return
      }

      if (Array.isArray(value)) {
        value.forEach((path) => {
          paths.push(path)
        })
      } else {
        paths.push(value)
      }
    })

    return paths
  }

  /**
   * Check the icon sizes.
   * Compare the standard sizes with the options specified sizes to get the sizes actually needed.
   *
   * @param {Array.<Number>} defaltSizes Sizes of the defalt.
   * @param {Object}         options     CLI options.
   * @param {String}         type        Type of the icon, 'ico' or 'icns'.
   *
   * @return {Array.<Number>} Checked sizes.
   */
  static checkImageSizes (defaltSizes, options, type) {
    return options && options.sizes && options.sizes[type] ? options.sizes[type] : defaltSizes
  }
}

